// Project Euler
// Problem 8 - largest product of 5 consecutive numbers
// Jacob Mintz


#include <stdio.h>
#include <stdio.h>

int main(void){
	int leng = 1000;
	char charNum[] = {""};
	int numArray[leng];
	//printf("Input Number:");
	//scanf("%s",charNum);
	printf("\n\nNumber:\n\n");
	
	int prodSize = 5;
	
	int i;
	for(i = 0; i<leng; i++){
		int numt = charNum[i] - '0';
		numArray[i] = numt;
		if(i<100){
			printf("%d",numArray[i],charNum[i]);
		if((i+1)%20==0)
			printf("\n");}
	}
	
	
	int arrayLeng = i;
	int largeProd = 0;
	int prod = 1;
	int startf;
	for(int f=0; f<(arrayLeng-prodSize); f++){
		for(int g=0; g<prodSize; g++)
			prod = numArray[f+g]*prod;
		if (largeProd < prod){
			largeProd = prod;
			startf = f;
		}
		prod = 1;
	}
	
	int t;
	for(int f=0; f<prodSize; f++){
		t = f+startf;
		printf("\n%d: %d",t,numArray[t]);
	}
	printf("\n\nProduct:%d\n",largeProd);
	
return 0;
}